mapper.select:
SELECT p.PRODUCT_ID, p.PRODUCT_TYPE_ID, p.PRODUCT_CODE, p.FACTORY_CODE, p.UNIT, p.CUSTOMS_CODE, p.USD_PRICE, p.CN_NAME, p.EN_NAME, p.VAT_RATE, p.BUY_PRICE, p.WEIGHT, p.VOLUME, p.TOP, p.BOTTOM, p.HEIGHT, p.LENGTH, p.WIDTH, p.PACK_HEIGHT, p.GW, p.PACKING_RATE, p.TAX_REBATE_RATE, p.CBM, p.PACKING, p.HD_MAP_URL, p.IS_DELETE, p.CREATE_TIME, p.MODIFY_TIME, p.CREATE_USER, p.MODIFY_USER, p.DESCRIPTION,p.THUMBNAIL,p.QR_CODE_PIC,
from product p left join product_type pt on p.PRODUCT_TYPE_ID = pt.PRODUCT_TYPE_ID where p.is_delete = 0 and p.product_id in
#{id}

SELECT a.PRODUCT_ID productId, a.PRODUCT_CODE productCode, a.CN_NAME cnName, a.TOP top, a.BOTTOM bottom, a.HEIGHT height, a.VOLUME volume, a.FACTORY_CODE factoryCode, b.CN_NAME AS typecn_name FROM product a LEFT JOIN product_type b ON a.PRODUCT_TYPE_ID = b.PRODUCT_TYPE_ID
join role_product rp on rp.PRODUCT_TYPE_ID = b.PRODUCT_TYPE_ID and rp.ROLE_ID = #{roleId}
a.IS_DELETE = 0 AND b.IS_DELETE = 0
and a.PRODUCT_CODE like CONCAT(#{productCode},'%')
and b.PRODUCT_TYPE_ID = #{parentId}
order by PRODUCT_ID desc
